import React, { useState, useEffect } from 'react';
import './RoofCalculator.css'; // Make sure to create this CSS file

const RoofCalculator = () => {
  const [pricePerSq, setPricePerSq] = useState('');  // Initialize to empty string
  const [sqFt, setSqFt] = useState('');  // Keep this as empty string
  const [waste, setWaste] = useState('');  // Initialize to empty string
  const [pitch, setPitch] = useState('0');
  const [warranty, setWarranty] = useState('Golden Pledge');
  const [homeType, setHomeType] = useState('Single-Story Home');
  const [adder, setAdder] = useState('Select an adder');
  const [squares, setSquares] = useState(0);  // Initialize squares to 0
  const [totalPrice, setTotalPrice] = useState(0);

  const pitchPrices = {
    '0-6': 0,
    '7-9': 35,
    '10-12': 55,
    '13+': 90
  };

  const homeTypePrices = {
    'Single-Story Home': 0,
    '2-Story Home (+$/sq)': 5,
    '3-Story Home (+$/sq)': 35
  };

  useEffect(() => {
    calculateTotal();
  }, [pricePerSq, sqFt, waste, pitch, warranty, homeType, adder]);

  const calculateTotal = () => {
    const wastePercentage = waste === '' ? 0 : parseInt(waste) / 100;
    const adjustedSquares = squares * (1 + wastePercentage);

    let total = adjustedSquares * pricePerSq;
    total += adjustedSquares * (pitchPrices[pitch] || 0);
    total += adjustedSquares * (homeTypePrices[homeType] || 0);

    // Add warranty cost (assuming it's a flat fee)
    total += warranty === 'Golden Pledge' ? 17 : 0; // Adjust this based on your actual warranty pricing

    // Adder calculation would go here if implemented

    setTotalPrice(isNaN(total) ? 0 : total);
  };

  const handlePricePerSqChange = (e) => {
    const value = e.target.value;
    // Allow empty string or any number
    if (value === '' || !isNaN(value)) {
      setPricePerSq(value);
    }
  };

  const handlePricePerSqBlur = () => {
    if (pricePerSq === '') {
      return;  // Keep it empty if no input
    }

    let value = parseInt(pricePerSq);
    if (isNaN(value) || value < 50) {
      setPricePerSq('50');
    } else if (value > 1000) {
      setPricePerSq('1000');
    } else {
      setPricePerSq(value.toString());
    }
  };

  const handleSqFtChange = (e) => {
    const value = e.target.value;
    // Allow empty string or any number
    if (value === '' || !isNaN(value)) {
      setSqFt(value);
      if (value !== '') {
        const numValue = parseInt(value);
        setSquares(Math.round(numValue / 100));
      } else {
        setSquares(0);  // Set squares to 0 when input is empty
      }
    }
  };

  const handleSqFtBlur = () => {
    if (sqFt === '') {
      setSquares(0);
      return;
    }

    let value = parseInt(sqFt);
    if (isNaN(value) || value < 100) {
      setSqFt('100');
      setSquares(1);
    } else if (value > 100000) {
      setSqFt('100000');
      setSquares(1000);
    } else {
      setSqFt(value.toString());
      setSquares(Math.round(value / 100));
    }
  };

  const handleWasteChange = (e) => {
    const value = e.target.value;
    // Allow empty string or any number
    if (value === '' || !isNaN(value)) {
      setWaste(value);
    }
  };

  const handleWasteBlur = () => {
    if (waste === '') {
      return;  // Keep it empty if no input
    }

    let value = parseInt(waste);
    if (isNaN(value) || value < 0) {
      setWaste('0');
    } else if (value > 50) {
      setWaste('50');
    } else {
      setWaste(value.toString());
    }
  };

  return (
    <div className="calculator">
      <h1>Roof Calculator</h1>

      <div className="input-group">
        <label>Price/Sq ($)</label>
        <input 
          type="number" 
          value={pricePerSq} 
          onChange={handlePricePerSqChange}
          onBlur={handlePricePerSqBlur}
          min="50"
          max="1000"
          step="1"
        />
      </div>

      <div className="input-group">
        <label>Sq/ft</label>
        <input 
          type="number" 
          value={sqFt} 
          onChange={handleSqFtChange}
          onBlur={handleSqFtBlur}
          min="100"
          max="100000"
          step="1"
        />
      </div>

      <div className="input-group">
        <label>Waste (%)</label>
        <input 
          type="number" 
          value={waste} 
          onChange={handleWasteChange}
          onBlur={handleWasteBlur}
          min="0"
          max="50"
          step="1"
        />
      </div>

      <div className="input-group">
        <label>Pitch</label>
        <select value={pitch} onChange={(e) => setPitch(e.target.value)}>
          <option value="0">0</option>
          <option value="0-6">0-6</option>
          <option value="7-9">7-9</option>
          <option value="10-12">10-12</option>
          <option value="13+">13+</option>
        </select>
      </div>

      {Object.entries(pitchPrices).map(([key, value]) => (
        <div key={key} className="pitch-price">
          <label>{key}:</label>
          <span>$ {value}</span>
        </div>
      ))}

      <div className="input-group">
        <label>Warranty</label>
        <select value={warranty} onChange={(e) => setWarranty(e.target.value)}>
          <option value="Golden Pledge">Golden Pledge</option>
          <option value="Silver Pledge">Silver Pledge</option>
        </select>
        <span>$ 17</span>
      </div>

      <div className="input-group">
        <label>Home Type</label>
        {Object.keys(homeTypePrices).map((type) => (
          <div key={type}>
            <input
              type="radio"
              id={type}
              name="homeType"
              value={type}
              checked={homeType === type}
              onChange={(e) => setHomeType(e.target.value)}
            />
            <label htmlFor={type}>{type}</label>
            {type !== 'Single-Story Home' && <span>$ {homeTypePrices[type]}</span>}
          </div>
        ))}
      </div>

      <div className="input-group">
        <label>Adders</label>
        <select value={adder} onChange={(e) => setAdder(e.target.value)}>
          <option value="Select an adder">Select an adder</option>
          {/* Add more adder options here */}
        </select>
      </div>

      <div className="results">
        <div>Squares: {squares}</div>
        <div>Total Price: ${totalPrice.toFixed(2)}</div>
      </div>
    </div>
  );
};

export default RoofCalculator;
