import React, { useState, useEffect } from 'react';
import './RoofCalculator.css'; // Make sure to create this CSS file

const RoofCalculator = () => {
  const [pricePerSq, setPricePerSq] = useState('');  // Initialize to empty string
  const [sqFt, setSqFt] = useState('');  // Keep this as empty string
  const [waste, setWaste] = useState('');  // Initialize to empty string
  const [pitch, setPitch] = useState('0-6');
  const [warranty, setWarranty] = useState('Golden Pledge');
  const [warrantyPrice, setWarrantyPrice] = useState('15');
  const [homeType, setHomeType] = useState('single');
  const [homeTypePrice, setHomeTypePrice] = useState('0');
  const [twoStoryPrice, setTwoStoryPrice] = useState('5');
  const [threeStoryPrice, setThreeStoryPrice] = useState('35');
  const [adder, setAdder] = useState('Select an adder');
  const [squares, setSquares] = useState(0);  // Initialize squares to 0
  const [totalPrice, setTotalPrice] = useState(0);
  const [pitchPrice, setPitchPrice] = useState('0');

  const pitchOptions = {
    '0-6': '0',
    '7-9': '35',
    '10-12': '55',
    '13+': '90'
  };

  const homeTypePrices = {
    'Single-Story Home': 0,
    '2-Story Home (+$/sq)': 5,
    '3-Story Home (+$/sq)': 35
  };

  const warrantyOptions = {
    'Golden Pledge': '15',
    'Silver Pledge': '11',
    'Systems': '8'
  };

  const calculateSquaresAndTotal = () => {
    if (sqFt === '') {
      setSquares(0);
      setTotalPrice(0);
      return;
    }

    const baseSquares = Math.round(parseInt(sqFt) / 100);
    const wastePercentage = waste === '' ? 0 : parseInt(waste) / 100;
    const adjustedSquares = Math.round(baseSquares * (1 + wastePercentage));
    setSquares(adjustedSquares);

    const pricePerSquare = pricePerSq === '' ? 0 : parseInt(pricePerSq);
    const pitchPricePerSquare = parseInt(pitchPrice);
    const warrantyPricePerSquare = parseInt(warrantyPrice);
    const homeTypePricePerSquare = parseInt(homeTypePrice);
    const calculatedTotal = adjustedSquares * (pricePerSquare + pitchPricePerSquare + warrantyPricePerSquare + homeTypePricePerSquare);
    setTotalPrice(calculatedTotal);
  };

  useEffect(() => {
    calculateSquaresAndTotal();
  }, [sqFt, waste, pricePerSq]);

  const handlePricePerSqChange = (e) => {
    const value = e.target.value;
    if (value === '' || !isNaN(value)) {
      setPricePerSq(value);
      calculateSquaresAndTotal();
    }
  };

  const handlePricePerSqBlur = () => {
    if (pricePerSq === '') return;
    let value = parseInt(pricePerSq);
    if (isNaN(value) || value < 50) {
      setPricePerSq('50');
    } else if (value > 1000) {
      setPricePerSq('1000');
    } else {
      setPricePerSq(value.toString());
    }
    calculateSquaresAndTotal();
  };

  const handleSqFtChange = (e) => {
    const value = e.target.value;
    if (value === '' || !isNaN(value)) {
      setSqFt(value);
      calculateSquaresAndTotal();
    }
  };

  const handleSqFtBlur = () => {
    if (sqFt === '') return;
    let value = parseInt(sqFt);
    if (isNaN(value) || value < 100) {
      setSqFt('100');
    } else if (value > 100000) {
      setSqFt('100000');
    } else {
      setSqFt(value.toString());
    }
    calculateSquaresAndTotal();
  };

  const handleWasteChange = (e) => {
    const value = e.target.value;
    if (value === '' || !isNaN(value)) {
      setWaste(value);
      calculateSquaresAndTotal();
    }
  };

  const handleWasteBlur = () => {
    if (waste === '') return;
    let value = parseInt(waste);
    if (isNaN(value) || value < 0) {
      setWaste('0');
    } else if (value > 50) {
      setWaste('50');
    } else {
      value = Math.round(value / 5) * 5;
      setWaste(value.toString());
    }
    calculateSquaresAndTotal();
  };

  const handlePitchChange = (e) => {
    const selectedPitch = e.target.value;
    setPitch(selectedPitch);
    setPitchPrice(pitchOptions[selectedPitch]);
    calculateSquaresAndTotal();
  };

  const handlePitchPriceChange = (e) => {
    const value = e.target.value;
    if (value === '' || !isNaN(value)) {
      setPitchPrice(value);
      calculateSquaresAndTotal();
    }
  };

  const handlePitchPriceBlur = () => {
    let value = parseInt(pitchPrice);
    if (isNaN(value) || value < 0) {
      setPitchPrice('0');
    } else if (value > 1000) {
      setPitchPrice('1000');
    } else {
      setPitchPrice(value.toString());
    }
    calculateSquaresAndTotal();
  };

  const handleWarrantyChange = (e) => {
    const selectedWarranty = e.target.value;
    setWarranty(selectedWarranty);
    setWarrantyPrice(warrantyOptions[selectedWarranty]);
    calculateSquaresAndTotal();
  };

  const handleWarrantyPriceChange = (e) => {
    const value = e.target.value;
    if (value === '' || !isNaN(value)) {
      setWarrantyPrice(value);
      calculateSquaresAndTotal();
    }
  };

  const handleWarrantyPriceBlur = () => {
    let value = parseInt(warrantyPrice);
    if (isNaN(value) || value < 0) {
      setWarrantyPrice('0');
    } else if (value > 1000) {
      setWarrantyPrice('1000');
    } else {
      setWarrantyPrice(value.toString());
    }
    calculateSquaresAndTotal();
  };

  const handleHomeTypeChange = (e) => {
    const selectedType = e.target.value;
    setHomeType(selectedType);
    if (selectedType === 'two-story') {
      setHomeTypePrice(twoStoryPrice);
    } else if (selectedType === 'three-story') {
      setHomeTypePrice(threeStoryPrice);
    } else {
      setHomeTypePrice('0');
    }
    calculateSquaresAndTotal();
  };

  return (
    <div className="calculator">
      <h1>Roof Calculator</h1>

      <div className="input-group">
        <label>Price/Sq ($)</label>
        <input 
          type="number" 
          value={pricePerSq} 
          onChange={handlePricePerSqChange}
          onBlur={handlePricePerSqBlur}
          min="50"
          max="1000"
          step="1"
        />
      </div>

      <div className="input-group">
        <label>Sq/ft</label>
        <input 
          type="number" 
          value={sqFt} 
          onChange={handleSqFtChange}
          onBlur={handleSqFtBlur}
          min="100"
          max="100000"
          step="1"
        />
      </div>

      <div className="input-group">
        <label>Waste (%)</label>
        <input 
          type="number" 
          value={waste} 
          onChange={handleWasteChange}
          onBlur={handleWasteBlur}
          min="0"
          max="50"
          step="5"
        />
      </div>

      <div className="input-group">
        <label>Pitch</label>
        <select value={pitch} onChange={handlePitchChange}>
          {Object.keys(pitchOptions).map(key => (
            <option key={key} value={key}>{key}</option>
          ))}
        </select>
        <div className="pitch-price-input">
          <span className="dollar-sign">$</span>
          <input 
            type="number" 
            value={pitchPrice} 
            onChange={handlePitchPriceChange}
            onBlur={handlePitchPriceBlur}
            min="0"
            max="1000"
            step="1"
          />
          <span>/Sq</span>
        </div>
      </div>

      <div className="input-group">
        <label>Warranty</label>
        <select value={warranty} onChange={handleWarrantyChange}>
          {Object.keys(warrantyOptions).map(key => (
            <option key={key} value={key}>{key}</option>
          ))}
        </select>
        <div className="warranty-price-input">
          <span className="dollar-sign">$</span>
          <input 
            type="number" 
            value={warrantyPrice} 
            onChange={handleWarrantyPriceChange}
            onBlur={handleWarrantyPriceBlur}
            min="0"
            max="1000"
            step="1"
          />
          <span>/Sq</span>
        </div>
      </div>

      <div className="input-group home-type-group">
        <label className="home-type-label">Home Type</label>
        <div className="home-type-options">
          <label className="home-type-option">
            <input 
              type="radio" 
              value="single" 
              checked={homeType === 'single'} 
              onChange={handleHomeTypeChange}
            />
            <span className="home-type-text">Single-Story Home</span>
          </label>
          <label className="home-type-option">
            <input 
              type="radio" 
              value="two-story" 
              checked={homeType === 'two-story'} 
              onChange={handleHomeTypeChange}
            />
            <span className="home-type-text">2-Story Home (+$/sq)</span>
            <span className="home-type-price">$ {twoStoryPrice}</span>
          </label>
          <label className="home-type-option">
            <input 
              type="radio" 
              value="three-story" 
              checked={homeType === 'three-story'} 
              onChange={handleHomeTypeChange}
            />
            <span className="home-type-text">3-Story Home (+$/sq)</span>
            <span className="home-type-price">$ {threeStoryPrice}</span>
          </label>
        </div>
      </div>

      <div className="input-group">
        <label>Adders</label>
        <select value={adder} onChange={(e) => setAdder(e.target.value)}>
          <option value="Select an adder">Select an adder</option>
          {/* Add more adder options here */}
        </select>
      </div>

      <div className="results">
        <div>Squares: {squares}</div>
        <div>Total Price: ${totalPrice.toFixed(2)}</div>
      </div>
    </div>
  );
};

export default RoofCalculator;
