import React, { useState, useMemo } from 'react';
import './App.css';

function App() {
  const [pricePerSquare, setPricePerSquare] = useState('100');
  const [squareFt, setSquareFt] = useState('');
  const [squareCount, setSquareCount] = useState('');
  const [pitch, setPitch] = useState('0');
  const [warranty, setWarranty] = useState('Systems');

  const pricePerSquareOptions = useMemo(() => 
    Array.from({ length: 701 }, (_, i) => (i + 100).toString()), 
  []);

  const pitchOptions = useMemo(() => 
    Array.from({ length: 14 }, (_, i) => i.toString()).concat(['13+']), 
  []);

  const squareCountOptions = useMemo(() => 
    Array.from({ length: 10000 }, (_, i) => (i + 1).toString()), 
  []);

  const warrantyOptions = useMemo(() => [
    { value: 'Systems', label: 'Systems (+$8/sq min, 5 yr, includes 5 yr Workmanship Warranty)' },
    { value: 'Silver', label: 'Silver (+$11/sq min, 2 yr, then transfers to GAF, includes 5 yr Workmanship Warranty)' },
    { value: 'Golden Pledge', label: 'Golden Pledge (+$15/sq min, 24 yr, includes 5 yr Workmanship Warranty)' },
  ], []);

  const calculateSquareCount = (ft) => {
    const count = ft / 100;
    return count % 1 >= 0.5 ? Math.ceil(count) : Math.floor(count);
  };

  const handleSquareFtChange = (e) => {
    const ft = e.target.value;
    setSquareFt(ft);
    const calculatedCount = calculateSquareCount(parseFloat(ft));
    setSquareCount(calculatedCount ? calculatedCount.toString() : '');
  };

  const getPitchAdder = (pitch) => {
    if (pitch >= 7 && pitch <= 9) return 35;
    if (pitch >= 10 && pitch <= 12) return 55;
    if (pitch === '13+') return 90;
    return 0;
  };

  const getWarrantyAdder = (warranty) => {
    switch (warranty) {
      case 'Systems': return 8;
      case 'Silver': return 11;
      case 'Golden Pledge': return 15;
      default: return 0;
    }
  };

  const totalPrice = useMemo(() => {
    if (!squareCount || !pricePerSquare) return 0;
    const basePrice = parseInt(squareCount) * parseFloat(pricePerSquare);
    const pitchAdder = getPitchAdder(pitch) * parseInt(squareCount);
    const warrantyAdder = getWarrantyAdder(warranty) * parseInt(squareCount);
    return basePrice + pitchAdder + warrantyAdder;
  }, [squareCount, pricePerSquare, pitch, warranty]);

  const handlePricePerSquareChange = (e) => {
    const value = e.target.value.replace(/\D/g, '');
    const numValue = parseInt(value, 10);

    if (value === '' || (numValue >= 50 && numValue <= 800)) {
      setPricePerSquare(value);
    }
  };

  const formatPricePerSquare = (value) => {
    if (value === '') return '$';
    return `$${value}`;
  };

  return (
    <div className="App">
      <h1>Roof Calculator</h1>
      <div>
        <label>
          Price/Sq:
          <div className="price-input-container">
            <input
              type="text"
              value={formatPricePerSquare(pricePerSquare)}
              onChange={handlePricePerSquareChange}
              onBlur={() => {
                if (pricePerSquare === '') setPricePerSquare('50');
              }}
              className="price-input"
            />
          </div>
        </label>
      </div>
      <div>
        <label>
          Square Ft:
          <input type="number" value={squareFt} onChange={handleSquareFtChange} />
        </label>
      </div>
      <div>
        <label>
          Square Count:
          <select value={squareCount} onChange={(e) => setSquareCount(e.target.value)}>
            <option value="">Select Square Count</option>
            {squareCountOptions.map(option => (
              <option key={option} value={option}>{option}</option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <label>
          Pitch:
          <select value={pitch} onChange={(e) => setPitch(e.target.value)}>
            {pitchOptions.map(option => (
              <option key={option} value={option}>{option}</option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <label>
          Warranty:
          <select value={warranty} onChange={(e) => setWarranty(e.target.value)}>
            {warrantyOptions.map(option => (
              <option key={option.value} value={option.value}>{option.label}</option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <h2>Total Price: {formatPricePerSquare(totalPrice.toFixed(2))}</h2>
      </div>
    </div>
  );
}

export default App;
